        return new Source("{% extends 'base.html.twig' %}

{% block title %}Article{% endblock %}

{% block content %}
    <h1 class=\"display-1 mt-5\">Article</h1>
    <hr class=\"my-4\"/>
    <div class=\"d-inline-block\">
        <h2>{{ post.title }}</h2>
        {% if post.premium %}
        <i class=\"premium-name\" style=\"color: goldenrod\">Premium</i>
        {% endif %}
    </div>
    <blockquote>De {{ post.author.firstname }} {{ post.author.lastname }}</blockquote>
    <hr>
    <div class=\"row\">
        <div class=\"col-md-5\">
            <img src=\"{{ post.illustration }}\" alt=\"{{ post.title }}\" class=\"img-fluid\">
        </div>
        <div class=\"col-md-7 my-auto\">
            <p>
                {{ post.description }}
            </p>
        </div>
    </div>
{% endblock %}", "posts/single_post.html.twig", "C:\\Users\\PC\\PhpstormProjects\\fidlelyTest\\la-boutique-francaise\\templates\\posts\\single_post.html.twig");
