    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Article premium{% endblock %}

{% block content %}
    <h1 class=\"display-1 mt-5 mb-5\">Article <strong class=\"premium-name\" style=\"color: goldenrod\">Premium</strong>
    <hr class=\"my-4\"/>
    <div class=\"d-inline-block\">
        <h2>{{ post.title }}</h2>
    </div>
    <blockquote>De {{ post.author.firstname }} {{ post.author.lastname }}</blockquote>
    <hr>
    <div class=\"row\">
        <div class=\"col-md-5\">
            <img src=\"{{ post.illustration }}\" alt=\"{{ post.title }}\" class=\"img-fluid\">
        </div>
        <div class=\"col-md-7 my-auto\">
            <p>
                {{ post.description }}
            </p>
            {% if post.premium %}
                <a href=\"{{ path('add_to_cart', {'id': post.id }) }}\">
              <button class=\"btn btn-dark btn-block\">Ajouté au panier</button>
            </a>
            {% endif %}
        </div>
    </div>
{% endblock %}", "posts/single_post_premium.html.twig", "C:\\Users\\PC\\PhpstormProjects\\fidlelyTest\\la-boutique-francaise\\templates\\posts\\single_post_premium.html.twig");
